mapper.select:
SELECT ref.`id`,ref.`driver_id`,ref.`driver_name`,ref.`seller_id`,ref.`seller_name`,ref.`tally_area_nos`,ref.`share_time` FROM user_driver_ref ref JOIN `register_bill` b ON ref.`seller_id`=b.`user_id` WHERE b.`verify_status`=20 AND b.is_checkin=-1 AND b.`is_deleted`=0 AND b.`bill_type`=10
AND ref.driver_id=#{driverId}
AND ref.seller_id=#{sellerId}
GROUP BY ref.driver_id

mapper.update:
update  user_driver_ref


        seller_name = #{sellerName,jdbcType=VARCHAR},



        tally_area_nos = #{tallyAreaNos,jdbcType=VARCHAR},


        share_time = #{shareTime},


where seller_id=#{sellerId}
and driver_id=#{driverId}
