mapper.update:
UPDATE user
SET credential_url =

    when #{user.thirdPartyCode} then #{user.credentialUrl}

WHERE third_party_code

    #{u.thirdPartyCode}

update third_hangguo_trade_data
set handle_flag=#{handleFlag}

    ,handle_remark = #{handleRemark}

where id

    #{trade.id}

mapper.select:
select c.id, c.id_card, c.user_id, c.user_name, c.tally_area_nos, c.check_no, c.check_org_code, c.check_org_name, c.check_result, c.check_time, c.check_type, c.checker, c.goods_name, c.goods_code, c.market_id, c.report_flag from check_order c where 1=1
and c.report_flag = #{reportFlag}
and c.id = #{id}

select id, name, type, operator_id, operator_name, data, created, modified, market_id from third_party_source_data where 1=1
AND name=#{name}
AND type=#{type}
AND id=#{id}

mapper.insert:
insert into third_party_source_data
(name,type,operator_id,operator_name,
    data,created, modified,market_id,report_flag)
values (
#{name},#{type},#{operatorId},#{operatorName},#{data},#{created},#{modified},#{marketId},#{reportFlag}
)

mapper.select:
SELECT r.`created` orderTime, r.`buyer_id` thirdBuyId, r.`id` thirdOrderId, r.`seller_id` thirdSellId,r.price
FROM trade_request r
JOIN trade_order o ON (r.trade_order_Id = o.id AND o.order_status = 10)
JOIN `user` u ON (r.`buyer_id` = u.`id` AND u.`source` = 20)
WHERE r.return_status = 0
AND r.source_type = 2
AND r.report_flag = 1

    AND o.modified <=#{modifiedEnd}


    AND o.modified >#{modifiedStart}


    AND r.buyer_market_id = #{marketId} AND r.seller_market_id = #{marketId}
