mapper.select:
SELECT b.id, b.id billId, b.`created`, b.`bill_type`, b.`regist_type`, b.`code`, b.`sample_code`, b.`register_source`, b.`name`, b.`corporate_name`, b.`plate`, b.`brand_id`, b.`brand_name`, b.`preserve_type`, b.`product_id`, b.`product_name`, b.`product_alias_name`, b.`origin_id`, b.`origin_name`, b.`weight`, b.`weight_unit`, b.`measure_type`, b.`piece_num`, b.`piece_weight`, b.`has_detect_report`, b.`has_origin_certifiy`, b.`has_handle_result`, b.`verify_status`, b.`detect_status`, b.`detect_request_id`, r.`sample_time`, r.`detect_source`, r.`detect_time`, r.`detect_result`, r.`detect_type`, r.`detector_name`, b.`check_sheet_id`, b.`checkin_status`, b.trade_account, b.trade_type_id, b.trade_type_name, b.is_print_checksheet, checkinout_record.`created` checkinCreated, detect_record.`normal_result` normalResult, detect_record.`pd_result` pdResult, b.`is_deleted`, b.`source_name`, b.`source_id` FROM register_bill b left join detect_request r ON (r.id = b.detect_request_id) left join detect_record on detect_record.detect_request_id=r.id LEFT JOIN checkinout_record ON checkinout_record.bill_id =r.id and checkinout_record.`inout` =10 and checkinout_record.status =10 WHERE 1 = 1
AND b.created >= #{createdStart}
AND b.created <= #{createdEnd}
AND checkinout_record.created >= #{checkinCreatedStart}
AND checkinout_record.created <= #{checkinCreatedEnd}
AND r.detect_time >= #{latestDetectTimeTimeStart}
AND r.detect_time <= #{latestDetectTimeTimeEnd}
AND b.register_source =#{registerSource}
AND b.verify_status =#{verifyStatus}
AND b.detect_status =#{detectStatus}
AND b.has_detect_report =#{hasDetectReport}
AND b.has_origin_certifiy =#{hasOriginCertifiy}
AND b.has_handle_result =#{hasHandleResult}
AND b.plate like '%${likePlate}%'
AND b.name like '%${likeName}%'
AND b.code like '%${likeCode}%'
AND b.phone like '%${likePhone}%'
AND b.product_name like '%${productName}%'
AND b.product_name like '%${likeProductName}%'
AND b.origin_name like '%${originName}%'
AND b.tally_area_no like '%${likeTallyAreaNo}%'
AND b.is_deleted =#{isDeleted}
AND r.detect_type =#{detectType}
AND r.detect_result =#{detectResult}
AND b.market_id =#{marketId}
AND b.regist_type =#{registType}
AND b.bill_type =#{billType}
AND b.name like '%${name}%'
AND b.sample_code like '%${likeSampleCode}%'
AND b.bill_type
#{billType}
group by b.id
