mapper.select:
SELECT * FROM (select bill_id,trade_detail.id as trade_detail_id, register_bill.verify_status,trade_detail.stock_weight,trade_detail.total_weight, trade_detail.weight_unit , trade_detail.trade_type, case when trade_detail.trade_type=0 then register_bill.created else trade_detail.created end as created, trade_detail.product_name, register_bill.checkin_status, register_bill.bill_type, register_bill.truck_type, register_bill.regist_type, register_bill.is_deleted, register_bill.detect_status from trade_detail left join register_bill on trade_detail.bill_id =register_bill.id where buyer_id=#{userId}
AND #{createdStart}<=created
AND created <=#{createdEnd}
AND trade_detail.product_name like '%${likeProductName}%'
AND register_bill.is_deleted =#{isDeleted}
AND register_bill.verify_status
#{verifyStatus}
AND register_bill.detect_status
#{detectStatus}
AND register_bill.bill_type
#{billType}
AND register_bill.market_id =#{marketId}
union select id as bill_id,null as trade_detail_id, register_bill.verify_status,weight,weight, weight_unit, null as trade_type, register_bill.created, register_bill.product_name, register_bill.checkin_status, register_bill.bill_type, register_bill.truck_type, register_bill.regist_type, register_bill.is_deleted, register_bill.detect_status from register_bill where user_id=#{userId} and id not in( select bill_id from trade_detail where buyer_id=#{userId} )
AND #{createdStart}<=register_bill.created
AND register_bill.created <=#{createdEnd}
AND register_bill.product_name like '%${likeProductName}%'
AND register_bill.is_deleted =#{isDeleted}
AND register_bill.verify_status
#{verifyStatus}
AND register_bill.detect_status
#{detectStatus}
AND register_bill.bill_type
#{billType}
AND register_bill.market_id =#{marketId}
) t order by created desc
