mapper.select:
select id from `user` where id not in(
select t.buyer_id from trade_request t, trade_order o where t.return_status != 20 and o.order_status=10

   AND #{createdStart}<=t.created 


  AND t.created <=#{createdEnd}

)
 AND `user`.qr_status <>#{qrStatus}

SELECT r.`created` orderTime, r.`buyer_id` thirdBuyId, r.`id` thirdOrderId, r.`seller_id` thirdSellId FROM trade_request r JOIN trade_order o ON (r.trade_order_Id = o.id AND o.order_status = 10) JOIN `user` u ON (r.`buyer_id` = u.`id` AND u.`source` = 20) WHERE r.return_status = 0
AND r.modified <=#{modifiedEnd}
AND r.modified >#{modifiedStart}

SELECT r.`created` orderTime, r.`buyer_id` thirdDsId, r.`id` thirdOrderId, r.`seller_id` thirdAccId FROM trade_request r JOIN trade_order o ON (r.trade_order_Id = o.id AND o.order_status = 10) JOIN `user` u ON (r.`buyer_id` = u.`id` AND u.`source` = 10) WHERE r.return_status = 0
AND r.modified <=#{modifiedEnd}
AND r.modified >#{modifiedStart}
