mapper.select:
select id from `user` where id not in(
select t.buyer_id from trade_request t, trade_order o where t.return_status != 20 and o.order_status=10

   AND #{createdStart}<=t.created 


  AND t.created <=#{createdEnd}

)
 AND `user`.qr_status <>#{qrStatus}

SELECT r.`created` orderTime, r.`buyer_id` thirdBuyId, r.`id` thirdOrderId, r.`seller_id` thirdSellId
  FROM trade_request r
  JOIN trade_order o ON (r.trade_order_Id = o.id AND o.order_status = 10)
  JOIN `user` u ON (r.`buyer_id` = u.`id` AND u.`source` = 20)
  WHERE r.return_status = 0

    AND o.modified <=#{modifiedEnd}


    AND o.modified >#{modifiedStart}


      AND r.buyer_market_id = #{marketId} AND r.seller_market_id = #{marketId}

SELECT r.`created` orderTime, r.`buyer_id` thirdDsId, r.`id` thirdOrderId, r.`seller_id` thirdAccId
  FROM trade_request r
  JOIN trade_order o ON (r.trade_order_Id = o.id AND o.order_status = 10)
  JOIN `user` u ON (r.`buyer_id` = u.`id` AND u.`source` = 10)
  WHERE r.return_status = 0

    AND o.modified <=#{modifiedEnd}


    AND o.modified >#{modifiedStart}


      AND r.buyer_market_id = #{marketId} AND r.seller_market_id = #{marketId}

SELECT GROUP_CONCAT(r.id) thirdOrderIds
FROM trade_request r
JOIN `user` u ON (r.`buyer_id` = u.`id` AND u.`source` = 20)
WHERE r.return_status = 20

  AND r.modified <=#{modifiedEnd}


  AND r.modified >#{modifiedStart}


      AND r.buyer_market_id = #{marketId} AND r.seller_market_id = #{marketId}

SELECT GROUP_CONCAT(r.id) thirdOrderIds
FROM trade_request r
JOIN `user` u ON (r.`buyer_id` = u.`id` AND u.`source` = 10)
WHERE r.return_status = 20

  AND r.modified <=#{modifiedEnd}


  AND r.modified >#{modifiedStart}


      AND r.buyer_market_id = #{marketId} AND r.seller_market_id = #{marketId}

SELECT t.`product_name` AS product_name, ROUND(SUM(IF(t.weight_unit=1,t.`trade_weight`*0.0005,t.trade_weight*0.001)),3) AS weight FROM `trade_request` t, trade_order o, `user` u WHERE o.`order_status`=10
AND t.`handle_time`>=#{startDate}
AND t.`handle_time`<=#{endDate}
AND t.product_name LIKE #{productName}
AND t.buyer_market_id = #{marketId} AND t.seller_market_id = #{marketId}
AND t.`return_status`!=20 AND t.trade_order_id = o.id AND u.id = t.buyer_id AND t.buyer_market_id = u.market_id AND u.state=1 AND u.validate_state=40 AND u.yn=1 GROUP BY t.`product_name` ORDER BY weight DESC

SELECT user.name AS user_name, t.`product_name` AS product_name, ROUND(SUM(IF(t.weight_unit=1,t.`trade_weight`*0.0005,t.trade_weight*0.001)),3) AS weight FROM `trade_request` t, trade_order o, `user` user WHERE o.`order_status`=10
AND t.`handle_time`>=#{startDate}
AND t.`handle_time`<=#{endDate}
and user.name like #{userName}
and user.phone like #{phone}
AND t.buyer_market_id = #{marketId} AND t.seller_market_id = #{marketId}
AND t.`return_status`!=20 AND t.trade_order_id = o.id AND user.id = t.buyer_id AND user.market_id = t.buyer_market_id AND user.state=1 AND user.validate_state=40 AND user.yn=1 GROUP BY t.buyer_id, t.`product_name` ORDER BY user.name, weight DESC
