mapper.select:
SELECT b.product_name varietyName, ROUND(0.0005*IFNULL(SUM(CASE WHEN b.weight_unit=1 THEN IFNULL(b.weight,0) ELSE 2*IFNULL(b.weight,0) END),0),3) weight, COUNT(1) approachBillCount FROM register_bill b JOIN `checkinout_record` c ON b.id=c.`bill_id` and b.market_id = c.market_id WHERE b.is_checkin=1 AND b.is_deleted=0
AND b.product_name like concat('%',#{likeVarietyName},'%')
AND c.created >= #{createdStart}
AND c.created <= #{createdEnd}
AND b.market_id = #{marketId}
GROUP BY b.product_name ORDER BY weight DESC

SELECT COUNT(DISTINCT b.user_id) userName, ROUND(0.0005 * IFNULL( SUM( CASE WHEN b.weight_unit = 1 THEN IFNULL(b.weight, 0) ELSE 2 * IFNULL(b.weight, 0) END ), 0 ), 3 ) weight, COUNT(1) billCount, COUNT(DISTINCT b.product_id) varietyCount FROM register_bill b JOIN `checkinout_record` c ON b.id=c.`bill_id` AND b.market_id = c.market_id JOIN user u ON b.user_id=u.id AND b.market_id = u.market_id WHERE b.is_checkin = 1 AND b.is_deleted = 0
AND b.name like concat('%',#{userName},'%')
AND u.phone like concat('%',#{phone},'%')
AND c.created >= #{startDate}
AND c.created <= #{endDate}
AND b.market_id = #{marketId}
ORDER BY weight DESC

SELECT IFNULL(u.name, '合计') userName, COUNT(1) billCount, IFNULL(b.product_name, '合计') varietyName, ROUND(0.0005 * IFNULL( SUM( CASE WHEN b.weight_unit = 1 THEN IFNULL(b.weight, 0) ELSE 2 * IFNULL(b.weight, 0) END ), 0 ), 3 ) weight FROM register_bill b JOIN `checkinout_record` c ON b.id=c.`bill_id` AND b.market_id = c.market_id JOIN `user` u ON b.user_id = u.id AND b.market_id = u.market_id WHERE b.is_checkin = 1 AND b.is_deleted = 0
AND b.name like concat('%',#{userName},'%')
AND b.product_name like concat('%',#{varietyName},'%')
AND u.phone like concat('%',#{phone},'%')
AND c.created >= #{startDate}
AND c.created <= #{endDate}
AND b.market_id = #{marketId}
GROUP BY b.user_id,b.product_name ORDER BY weight DESC
