mapper.select:
SELECT b.product_name varietyName, ROUND(0.0005*IFNULL(SUM(CASE WHEN b.weight_unit=1 THEN IFNULL(b.weight,0) ELSE 2*IFNULL(b.weight,0) END),0),2) weight, COUNT(1) approachBillCount FROM register_bill b JOIN `checkinout_record` c ON b.id=c.`bill_id` WHERE b.is_checkin=1 AND b.is_deleted=0 AND b.verify_status=20
AND b.product_name like concat('%',#{likeVarietyName},'%')
AND c.created >= #{createdStart}
AND c.created <= #{createdEnd}
GROUP BY b.product_name ORDER BY weight DESC

SELECT COUNT(DISTINCT b.name) userName, ROUND(0.0005 * IFNULL( SUM( CASE WHEN b.weight_unit = 1 THEN IFNULL(b.weight, 0) ELSE 2 * IFNULL(b.weight, 0) END ), 0 ), 2 ) weight, COUNT(1) billCount, COUNT(DISTINCT b.product_id) varietyCount FROM register_bill b JOIN `checkinout_record` c ON b.id=c.`bill_id` JOIN user u ON b.user_id=u.id WHERE b.is_checkin = 1 AND b.is_deleted = 0 AND b.verify_status = 20
AND b.name like concat('%',#{userName},'%')
AND u.phone like concat('%',#{phone},'%')
AND c.created >= #{startDate}
AND c.created <= #{endDate}
ORDER BY weight DESC

SELECT IFNULL(b.name, '合计') userName, COUNT(1) billCount, IFNULL(b.product_name, '合计') varietyName, ROUND(0.0005 * IFNULL( SUM( CASE WHEN b.weight_unit = 1 THEN IFNULL(b.weight, 0) ELSE 2 * IFNULL(b.weight, 0) END ), 0 ), 2 ) weight FROM register_bill b JOIN `checkinout_record` c ON b.id=c.`bill_id` JOIN user u ON b.user_id = u.id WHERE b.is_checkin = 1 AND b.is_deleted = 0 AND b.verify_status = 20
AND b.name like concat('%',#{userName},'%')
AND u.phone like concat('%',#{phone},'%')
AND c.created >= #{startDate}
AND c.created <= #{endDate}
GROUP BY b.name,b.product_name
