mapper.select:
SELECT validate_state validateState,COUNT(1) numbers from `user` u where yn = 1
and market_id = #{marketId}
GROUP BY validate_state

SELECT id,name,tally_area_nos,phone,validate_state
from `user` u
where yn=1

    and validate_state = #{validateState}


    and market_id = #{marketId}


    and (name LIKE CONCAT('%',#{keyword},'%') or tally_area_nos LIKE CONCAT('%',#{keyword},'%'))

select qr_status,count(1) cnt from `user`
where yn=1

    AND market_id = #{marketId}


    and qr_status

        #{qrStatus}



group by qr_status

select * from user_store where 1=1

    AND (user_name #{queryCondition} or store_name like #{queryCondition})


    AND u.market_id = #{marketId}

SELECT u.`id`, u.`third_party_code` FROM `user` u JOIN `user_ext` e ON u.id = e.user_id WHERE u.`yn` = 1
and market_id = #{marketId}
AND e.credential_url IS NULL AND e.supplier_type IS NOT NULL

select *
from `user` u
where u.id in (
    select user_info_id
    from user_qr_history uq
    where uq.is_valid = #{isValid}
    AND uq.created >= #{createdStart} 
  )
